package utils

import (
	"testing"

	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

const stasUtxo = ""
const invalidStasUtxo = ""

func Test_GetLockingScriptFromSTASLockingScript(t *testing.T) {
	t.Run("empty key", func(t *testing.T) {
		_, err := GetLockingScriptFromSTASLockingScript("")
		require.ErrorIs(t, err, ErrCouldNotDetermineDestinationOutput)
	})

	t.Run("destination", func(t *testing.T) {
		lockingScript, err := GetLockingScriptFromSTASLockingScript(stasUtxo)
		require.NoError(t, err)
		assert.Equal(t, "76a91436db940fb5948e688a959c3e72ec385a7f6f67cc88ac", lockingScript)
	})

	t.Run("invalid stas token", func(t *testing.T) {
		_, err := GetLockingScriptFromSTASLockingScript(invalidStasUtxo)
		require.ErrorIs(t, err, ErrCouldNotDetermineDestinationOutput)
	})
}
